import React, { useState } from 'react';
import data from './data/recipes.json';  // Importation des données (recettes)

// Composant principal de recherche de recettes
function RecipeSearch() {
  // 1. Déclaration de l'état 'query' pour la recherche
  const [query, setQuery] = useState("");  // Initialisation de la valeur du champ de recherche

  // 2. Fonction qui gère la modification du champ de recherche
  const handleSearchChange = (event) => {
    setQuery(event.target.value);  // Met à jour l'état 'query' avec la valeur de l'input
  };

  // 3. Filtrage des recettes en fonction de la recherche
  const filteredRecipes = data.filter(post => 
    query === '' || (post.ingredi && post.name.toLowerCase().includes(query.toLowerCase())) // Filtrer par nom
  );


  // 4. Rendu du composant
  return (
    <div>
      {/* 5. Champ de recherche */}
      <input 
        type="text" 
        value={query} // La valeur du champ est liée à l'état 'query'
        onChange={handleSearchChange} // On met à jour 'query' à chaque modification de l'input
        placeholder="Rechercher une recette..." // Texte d'instruction dans l'input
      />

      {/* 6. Affichage des recettes filtrées */}
      <div>
        {filteredRecipes.map((post, index) => (
          <div className="box" key={index}> {/* Utilisation de l'index comme clé */}
            <p>{post.name}</p> {/* Affichage du nom de la recette */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecipeSearch;
