import './IngredientInput.css';
import mascotteBiscottine from '../assets/mascotte.png'
import LogoInputIngredient from '../assets/logo.png'
import React, { useState, useEffect } from 'react';
import recipes from './recipes.json'; // Importation des données JSON
import './IngredientInput.css'; // Fichier CSS pour le style, optionnel






function ingredientInput() {
    const [inputText, setInputText] = useState('');
    const [debouncedInput, setDebouncedInput] = useState(inputText);
    const [filteredRecipes, setFilteredRecipes] = useState(recipes);
  
    // Utilisation d'un useEffect pour gérer le debounce (délai de mise à jour)
    useEffect(() => {
      const timer = setTimeout(() => {
        setDebouncedInput(inputText.toLowerCase());
      }, 300); // Délai de 300ms pour éviter un filtrage trop rapide
  
      return () => clearTimeout(timer); // Nettoyage du timer à chaque modification
    }, [inputText]);
  
    // Filtrer les recettes selon l'entrée utilisateur
    useEffect(() => {
      if (debouncedInput === '') {
        setFilteredRecipes(recipes); // Si pas de recherche, afficher toutes les recettes
      } else {
        const filtered = recipes.filter(recipe =>
          recipe.name.toLowerCase().includes(debouncedInput)
        );
        setFilteredRecipes(filtered);
      }
    }, [debouncedInput]);
  
    // Gestion de l'entrée utilisateur
    const handleInputChange = (e) => {
      setInputText(e.target.value);
    };


    return (

        <div className="encadrementIngredientInput">

            
{/* Image de la mascotte  */}
                <img className='biscottineMascotteInput' src={mascotteBiscottine} alt='' />
               {/* Image logo  */}
                <img className='titreLogoInput' src={LogoInputIngredient} alt='' />
            {/* Texte entredessous du Logo  */}
                <p className='texteSousLogoInput'>Trouve-moi <br/> une recette !</p>
           
            <p></p>
            <p></p>
            <input className="ingredientInput" type="text" placeHolder=" Yes"
             on onChange={handleInputChange} />



        </div>

    )
};


export default ingredientInput;
