import './IngredientInput.css';
import mascotteBiscottine from '../assets/mascotte.png'
import logoInputIngredient from '../assets/logo.png'
import data  from './data/recipes.json'


function ingredientInput() {

// data.map((post) => (
//     <div key={post.title}>
//         <likey=> {ingredients.name}</likey=>.
//     </div>
// ))

return data.map((post) => (
    <div key={post.title}> {/* 'key' pour le conteneur de chaque recette */}
      <h3>{post.title}</h3> {/* Affichage du titre de la recette */}
      <ul>
        {post.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient.name}</li> // 'key' pour chaque ingrédient
        ))}
      </ul>
      <input placeholder="Enter Post Title"/>
    </div>
  ));

