//             {/* Texte entredessous du Logo  */}
//                 <p className='texteSousLogoInput'>Trouve-moi <br/> une recette !</p>
           
//             <p></p>
//             <p></p>
//             <input className="ingredientInput" type="text" placeHolder=" Yes" />



//         </div>

    
};


export default ingredientInput;
