import React, { useState } from 'react';
import data from './data/recipes.json';  // Importation des données (recettes)

// Composant principal de recherche de recettes
function RecipeSearch() {
  // Déclaration de l'état 'query' pour la recherche
  const [query, setQuery] = useState("");  // Initialisation de la valeur du champ de recherche

  // Fonction qui gère la modification du champ de recherche
  const handleSearchChange = (event) => {
    setQuery(event.target.value);  // Met à jour 'query' avec la valeur de l'input
  };

  // Filtrage des recettes en fonction de la recherche
  const filteredRecipes = data.filter(post => 
    query === '' || (post.title && post.title.toLowerCase().includes(query.toLowerCase()))
  );

  // Rendu du composant
  return (
    <div>
      {/* Champ de recherche */}
      <input 
        type="text" 
        value={query} // La valeur du champ est liée à l'état 'query'
        onChange={handleSearchChange} // On met à jour 'query' à chaque modification de l'input
        placeholder="Rechercher une recette..." // Texte d'instruction dans l'input
      />

      {/* Affichage des ingrédients des recettes filtrées */}
      <div>
        {filteredRecipes.map((post, index) => (
          <div key={index} className="recipe">
            <h3>{post.title}</h3> {/* Affichage du titre de la recette */}
            
            {/* Affichage des ingrédients pour chaque recette */}
            <ul>
              {post.ingredients.map((ingredient, idx) => (
                <li key={idx}>
                  {ingredient.name} - {ingredient.quantity} {ingredient.unit}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecipeSearch;
