import React from 'react';
import './RecipeResults.css';
import data from '../data/recipes.json';


// function qui crée une carte pour chaque entité du tableau data 
function RecipeCard(props) {
        return (        
            <div className="RecipeResults-card">
    
                <img src={props.url} className="RecipeResults-card-img"/> 
    
                <div className="RecipeResults-card-info">
                    <div className="RecipeResults-card-title">{props.title}</div>
                    <div className="RecipeResults-card-preparation">{props.preparation_time}</div>
                </div>
            </div>
        )
      
}

// Function pour peupler le div container RecipeResults
export default function RecipeResults() {

    const filteredRecipes = data.filter((recipe) => {
        const recetteIngredients = recipe.ingredients.map(ing => ing.name.toLowerCase());
  
        // Vérifie si tous les ingrédients gardés sont dans la recette
        const contientIngredientsGarder = ingredientGarder.every((ingredient) => 
            recetteIngredients.includes(ingredient.name.toLowerCase())
        );
  
        // Vérifie que la recette ne contient pas d'ingrédients évités
        const contientIngredientsEviter = ingredientEviter.some((ingredient) =>
            recetteIngredients.includes(ingredient.name.toLowerCase())
        );
  
        // Retourne vrai si la recette contient tous les ingrédients gardés et aucun évité
        return contientIngredientsGarder && !contientIngredientsEviter;
    });
    return (
        <div className="RecipeResults">

        {/* {data.map((carte, index) => (
                <RecipeCard key={index}
                 url={carte.url} 
                 title={carte.title} 
                 preparation_time={carte.preparation_time} 
                 tags={carte.tags} />
            ))} */}
            {filteredRecipes.length > 0 ? (
                <div className="recipe-list">
                    {filteredRecipes.map((recipe, index) => (
                        <RecipeCard
                            key={index}
                            url={recipe.url}
                            title={recipe.title}
                            preparation_time={recipe.preparation_time}
                            tags={recipe.tags}
                        />
                    ))}
                </div>
            ) : (
                <p>Aucune recette ne correspond aux critères sélectionnés.</p>
            )}

        </div>
    )
}
