import React, { useState } from 'react';
import data from './data/recipes.json'; // Import des données

function IngredientSearch() {
  const [query, setQuery] = useState(""); // État pour la recherche
  const [keptIngredients, setKeptIngredients] = useState([]); // État pour les ingrédients à garder
  const [avoidedIngredients, setAvoidedIngredients] = useState([]); // État pour les ingrédients à éviter

  // Fonction pour gérer la recherche
  const handleSearchChange = (event) => {
    setQuery(event.target.value);
  };

  // Fonction pour gérer les choix de l'utilisateur (garder ou éviter un ingrédient)
  const handleIngredientChoice = (ingredient, choice) => {
    if (choice === "keep") {
      setKeptIngredients((prev) => [...prev, ingredient]);
      setAvoidedIngredients((prev) => prev.filter(item => item.name !== ingredient.name)); // Retirer de la liste évitée
    } else if (choice === "avoid") {
      setAvoidedIngredients((prev) => [...prev, ingredient]);
      setKeptIngredients((prev) => prev.filter(item => item.name !== ingredient.name)); // Retirer de la liste gardée
    }
  };

  // Extraire tous les ingrédients de toutes les recettes
  const allIngredients = data.flatMap(post => post.ingredients);

  // Filtrer les ingrédients selon la recherche
  const filteredIngredients = allIngredients.filter(ingredient => 
    query === '' || ingredient.name.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <div>
      {/* Champ de recherche pour filtrer les ingrédients */}
      <input 
        type="text" 
        value={query} 
        onChange={handleSearchChange} 
        placeholder="Rechercher un ingrédient..." 
      />

      {/* Afficher uniquement si la recherche n'est pas vide */}
      {query !== '' && (
        <div className="ingredients-list">
          {filteredIngredients.map((ingredient, index) => (
            <div key={index} className="ingredient-card">
              {/* Affichage de l'image et du nom de l'ingrédient */}
              <img src={ingredient.image} alt={ingredient.name} className="ingredient-image" />
              <p>{ingredient.name}</p>

              {/* Boutons pour garder ou éviter */}
              <button 
                onClick={() => handleIngredientChoice(ingredient, "keep")}
                disabled={keptIngredients.some(item => item.name === ingredient.name)}
              >
                Garder
              </button>
              <button 
                onClick={() => handleIngredientChoice(ingredient, "avoid")}
                disabled={avoidedIngredients.some(item => item.name === ingredient.name)}
              >
                Éviter
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Affichage des ingrédients gardés et évités */}
      {query !== '' && (
        <div className="selections">
          <h3>Ingrédients gardés :</h3>
          <ul>
            {keptIngredients.map((ingredient, index) => (
              <li key={index}>{ingredient.name}</li>
            ))}
          </ul>
          <h3>Ingrédients évités :</h3>
          <ul>
            {avoidedIngredients.map((ingredient, index) => (
              <li key={index}>{ingredient.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default IngredientSearch;
